import React, { useState } from 'react';
import axios from 'axios';

function ResetPassword() {
  const [formData, setFormData] = useState({ email: '', otp: '', new_password: '' });
  const [error, setError] = useState(''); // Define state for error messages
  const [success, setSuccess] = useState(''); // Define state for success messages

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Send the entire formData object in the request
      const response = await axios.post('http://localhost:8000/api/reset-password/', formData);
      setSuccess('Password is reset.');
      setError('');
    } catch (error) {
      const errorMessage = error.response?.data?.non_field_errors?.[0] || 'An error occurred.';
      setError(errorMessage);
      setSuccess('');
    }
  };

  return (
    <div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="otp"
          placeholder="OTP"
          value={formData.otp}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="new_password"
          placeholder="New Password"
          value={formData.new_password}
          onChange={handleChange}
          required
        />
        <button type="submit">Reset Password</button>
      </form>
    </div>
  );
}

export default ResetPassword;
