import React, { useState } from 'react';
import api from '/api'; // Import the axios instance
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '',
    mobile: '',
    email: '',
    dob: '',
    gender: '',
    address: '',
    password: '',
  });
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('users/signup/', formData); // Use the base URL
      setOtp(response.data.otp); // Show OTP for verification
      navigate('/login');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />
      <input type="text" name="mobile" value={formData.mobile} onChange={handleChange} placeholder="Mobile" required />
      <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
      <input type="date" name="dob" value={formData.dob} onChange={handleChange} placeholder="Date of Birth" required />
      <input type="text" name="gender" value={formData.gender} onChange={handleChange} placeholder="Gender" required />
      <textarea name="address" value={formData.address} onChange={handleChange} placeholder="Address" required></textarea>
      <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" required />
      <button type="submit">Sign Up</button>
    </form>
  );
};

export default Signup;
