import React, { useState } from 'react';
import axios from 'axios';

function ProfileUpdate() {
  const [formData, setFormData] = useState({
    email: '',
    name: '',
    mobile: '',
    dob: '',
    gender: '',
    address: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Retrieve token from local storage or state management
    const token = localStorage.getItem('token'); // Adjust this line as necessary

    try {
      const response = await axios.put(
        'http://localhost:8000/api/profile-update/',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}` // Add token here
          }
        }
      );
      setSuccess('Profile updated successfully.');
      setError('');
    } catch (error) {
      const errorMessage = error.response?.data?.detail || 'An error occurred.';
      setError(errorMessage);
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
      <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
      <input type="text" name="mobile" placeholder="Mobile" value={formData.mobile} onChange={handleChange} required />
      <input type="date" name="dob" placeholder="Date of Birth" value={formData.dob} onChange={handleChange} required />
      <select name="gender" value={formData.gender} onChange={handleChange} required>
        <option value="">Select Gender</option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
        <option value="Other">Other</option>
      </select>
      <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
      <button type="submit">Update Profile</button>

      {error && <div style={{ color: 'red' }}>{error}</div>}
      {success && <div style={{ color: 'green' }}>{success}</div>}
    </form>
  );
}

export default ProfileUpdate;
