import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('api/login/', { email, password });
      console.log('Login successful:', response.data);
      localStorage.setItem('token', response.data.token); // If your API returns a token
      navigate('/profile'); // Redirect to the profile page or any other page on successful login
    } catch (error) {
      console.error('Error logging in:', error.response?.data || error.message);
      setMessage(`Error: ${error.response?.data.detail || 'An error occurred'}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
        required
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
        required
      />
      <button type="submit">Login</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default Login;
