import React, { useState } from 'react';
import axios from 'axios';

function ProfileUpdate() {
  const [formData, setFormData] = useState({
    email: '',
    name: '',
    mobile: '',
    dob: '',
    gender: '',
    address: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Update formData based on user input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    // Retrieve the authentication token from local storage
    const token = localStorage.getItem('authToken');

    try {
      // Make the PUT request with the token included in the headers
      const response = await axios.put(
        'http://localhost:8000/api/profile-update/',
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}` // Include the token here
          }
        }
      );
      // Handle successful response
      setSuccess('Profile updated successfully.');
      setError('');
    } catch (error) {
      // Handle errors
      const errorMessage = error.response?.data?.detail || 'An error occurred.';
      setError(errorMessage);
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
      <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} required />
      <input type="date" name="dob" placeholder="Date of Birth" onChange={handleChange} required />
      <input type="text" name="gender" placeholder="Gender" onChange={handleChange} required />
      <textarea name="address" placeholder="Address" onChange={handleChange} required></textarea>
      <button type="submit">Update Profile</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
    </form>
  );
}

export default ProfileUpdate;
