import React, { useState } from 'react';
import axios from 'axios';

const ResetPassword = () => {
  const [email, setEmail] = useState(''); // Define state for email
  const [error, setError] = useState(''); // Define state for error messages
  const [success, setSuccess] = useState(''); // Define state for success messages

  // Handle changes to the email input
  const handleChange = (e) => {
    setEmail(e.target.value);
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Make the POST request to reset the password
      const response = await axios.post('http://localhost:8000/api/reset-password/', { email });
      setSuccess('Password reset link has been sent to your email.');
      setError('');
    } catch (error) {
      // Set error message if the request fails
      setError(error.response?.data?.detail || 'An error occurred during password reset.');
      setSuccess('');
    }
  };

  return (
    <div>
      <h2>Reset Password</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Send Reset Link</button>
      </form>
    </div>
  );
};

export default ResetPassword;
