import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    axios.post('http://localhost:8000/api/login/', formData)
      .then(response => {
        // Assuming the response contains the token in response.data.token
        const token = response.data.token;

        if (token) {
          // Store token in localStorage
          localStorage.setItem('authToken', token);

          // Provide feedback and redirect
          alert(response.data.message);
          window.location.href = '/api/profile-update';  // Redirect to profile update
        } else {
          setError('Login failed: Token not received.');
        }
      })
      .catch(error => {
        console.error('Login error:', error);
        setError('Login failed: Invalid credentials or server error.');
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
      <button type="submit">Log In</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <a href="/api/forgot-password">Forgot Password?</a>
    </form>
  );
}

export default Login;






















