import React, { useState, useEffect } from 'react';
import api from '../api';

const ProfileUpdate = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await api.get('profile-update/', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setUser(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchUser();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.put('profile-update/', user, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      // Notify user of successful profile update
    } catch (error) {
      console.error(error);
    }
  };

  if (!user) return <div>Loading...</div>;

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" value={user.name} onChange={handleChange} placeholder="Name" required />
      <input type="text" name="mobile" value={user.mobile} onChange={handleChange} placeholder="Mobile" required />
      <input type="email" name="email" value={user.email} onChange={handleChange} placeholder="Email" required />
      <input type="date" name="dob" value={user.dob} onChange={handleChange} placeholder="Date of Birth" required />
      <input type="text" name="gender" value={user.gender} onChange={handleChange} placeholder="Gender" required />
      <textarea name="address" value={user.address} onChange={handleChange} placeholder="Address" required></textarea>
      <button type="submit">Update Profile</button>
    </form>
  );
};

export default ProfileUpdate;
