import React, { useState } from 'react';
import api from '../api';

const ResetPassword = () => {
  const [formData, setFormData] = useState({
    email: '',
    newPassword: '',
    confirmPassword: '',
    otp: ''
  });

  const [message, setMessage] = useState('');
  const [otpSent, setOtpSent] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleOtpRequest = async () => {
    try {
      await api.post('api/request-otp/', { email: formData.email });
      setOtpSent(true);
      setMessage('OTP sent to your email');
    } catch (error) {
      console.error('Error requesting OTP:', error.response.data);
      setMessage(`Error: ${error.response.data.error}`);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('api/reset-password/', formData);
      setMessage('Password reset successful');
    } catch (error) {
      console.error('Error resetting password:', error.response.data);
      setMessage(`Error: ${error.response.data.error}`);
    }
  };

  return (
    <div>
      <h1>Reset Password</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>
        {otpSent && (
          <>
            <div>
              <label>OTP:</label>
              <input
                type="text"
                name="otp"
                value={formData.otp}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>New Password:</label>
              <input
                type="password"
                name="newPassword"
                value={formData.newPassword}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Confirm Password:</label>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
              />
            </div>
          </>
        )}
        <button type="button" onClick={handleOtpRequest}>
          {otpSent ? 'Resend OTP' : 'Send OTP'}
        </button>
        {otpSent && <button type="submit">Reset Password</button>}
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default ResetPassword;
