// src/App.js

import React, { useState } from 'react';
import Signup from './components/Signup';
import Login from './components/Login';
import ForgotPassword from './components/ForgotPassword';
import ResetPassword from './components/ResetPassword';
import ProfileUpdate from './components/ProfileUpdate';
import './index.css'; // Import the CSS file

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('accessToken') || '');
  const [view, setView] = useState('login');

  const renderForm = () => {
    switch (view) {
      case 'signup':
        return <Signup />;
      case 'login':
        return <Login setToken={setToken} />;
      case 'forgotPassword':
        return <ForgotPassword />;
      case 'resetPassword':
        return <ResetPassword />;
      case 'profile':
        return <ProfileUpdate token={token} />;
      default:
        return <Login setToken={setToken} />;
    }
  };

  return (
    <div className="container">
      <h1>React + Django Auth</h1>
      <nav>
        <button onClick={() => setView('signup')}>Signup</button>
        <button onClick={() => setView('login')}>Login</button>
        <button onClick={() => setView('forgotPassword')}>Forgot Password</button>
        <button onClick={() => setView('resetPassword')}>Reset Password</button>
        <button onClick={() => setView('profile')}>Profile</button>
      </nav>
      {renderForm()}
    </div>
  );
};

export default App;
