// src/components/Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    mobile: '',
    dob: '',
    gender: '',
    address: '',
  });

  const [otp, setOtp] = useState('');
  const [showOtpField, setShowOtpField] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/signup/', formData);
      console.log(response.data);
      setShowOtpField(true);
      alert('OTP sent to your email.');
    } catch (error) {
      console.error(error);
      alert('Signup failed. Please try again.');
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/verify-otp/', { otp });
      console.log(response.data);
      alert('Signup successful!');
      navigate('/login'); // Redirect to login after successful signup
    } catch (error) {
      console.error(error);
      alert('OTP verification failed. Please try again.');
    }
  };

  return (
    <div>
      {!showOtpField ? (
        <form onSubmit={handleSubmit}>
          <input type="text" name="username" placeholder="Username" onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
          <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} required />
          <input type="date" name="dob" placeholder="Date of Birth" onChange={handleChange} required />
          <select name="gender" onChange={handleChange} required>
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
          <textarea name="address" placeholder="Address" onChange={handleChange}></textarea>
          <button type="submit">Signup</button>
        </form>
      ) : (
        <form onSubmit={handleOtpSubmit}>
          <input type="text" name="otp" placeholder="Enter OTP" onChange={(e) => setOtp(e.target.value)} required />
          <button type="submit">Verify OTP</button>
        </form>
      )}
    </div>
  );
};

export default Signup;
